import { useEffect, useState } from 'react';
import { supabase } from '../supabaseClient';
import ProductCard from '../components/ProductCard';
import AddProduct from '../components/AddProduct';
import Navbar from '../components/Navbar';
import Footer from '../components/Footer';

const Crewneck = () => {
  const [products, setProducts] = useState([]);

  useEffect(() => {
    const fetchProducts = async () => {
      const { data } = await supabase.from('products').select('*').eq('category', 'crewneck');
      setProducts(data);
    };
    fetchProducts();
  }, []);

  return (
    <div>
      <Navbar />
      <div className="bg-white py-8">
        <div className="max-w-6xl mx-auto px-6">
          <h1 className="font-poppins font-bold text-4xl md:text-5xl text-black text-center mb-4">
            Crewneck T-Shirts
          </h1>
          <p className="font-open-sans text-gray-500 text-lg md:text-xl text-center max-w-2xl mx-auto mb-8">
            Explore our comfortable Crewneck T-shirts.
          </p>
          <AddProduct category="crewneck" />
          <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
            {products.length === 0 ? (
              <p className="font-open-sans text-gray-500 text-center col-span-3">
                No products available
              </p>
            ) : (
              products.map((product) => <ProductCard key={product.id} product={product} />)
            )}
          </div>
        </div>
      </div>
      <Footer />
    </div>
  );
};

export default Crewneck;
